<?phprequire_once($_SERVER['DOCUMENT_ROOT'] ."/fileupload/PHPInclude/Orders.php");function FormatOrderAsHTML($order, $useViewOrderKey = true, $includeViewLink = false){	global $OrdersContext;	$files = array();	if($order->OrderId != 0)	{		$files = $OrdersContext->GetFiles($order->OrderId);		$HTML = '<h4>Order</h4>    <hr />    <dl class="row">        <dt class="col-sm-2">            Order Id        </dt>        <dd class="col-sm-10">            '.htmlspecialchars($order->OrderId).'        </dd>        <dt class="col-sm-2">            Contact Name        </dt>        <dd class="col-sm-10">            '. htmlspecialchars($order->ContactName).'        </dd>        <dt class="col-sm-2">            Phone Number        </dt>        <dd class="col-sm-10">            '. htmlspecialchars($order->PhoneNumber).'        </dd>        <dt class="col-sm-2">            Email Address        </dt>        <dd class="col-sm-10">            '. htmlspecialchars($order->EmailAddress).'        </dd>        <dt class="col-sm-2">            Company Name        </dt>        <dd class="col-sm-10">            '. htmlspecialchars($order->CompanyName).'        </dd>        <dt class="col-sm-2">            Address 1        </dt>        <dd class="col-sm-10">            '. htmlspecialchars($order->Address1).'        </dd>        <dt class="col-sm-2">            Address 2        </dt>        <dd class="col-sm-10">            '. htmlspecialchars($order->Address2).'        </dd>        <dt class="col-sm-2">            City        </dt>        <dd class="col-sm-10">            '. htmlspecialchars($order->City).'        </dd>        <dt class="col-sm-2">            State        </dt>        <dd class="col-sm-10">            '. htmlspecialchars($order->State).'        </dd>        <dt class="col-sm-2">            Zip Code        </dt>        <dd class="col-sm-10">            '. htmlspecialchars($order->ZipCode).'        </dd>        <dt class="col-sm-2">            Date Due        </dt>        <dd class="col-sm-10">            '. htmlspecialchars($order->DueDate).'        </dd>        <dt class="col-sm-2">            Time Due        </dt>        <dd class="col-sm-10">            '. htmlspecialchars($order->DueTime).'        </dd>		<dt class="col-sm-2">            Date Created        </dt>        <dd class="col-sm-10">            '. htmlspecialchars($order->DateCreated).'        </dd>                <dt class="col-sm-2">            Project Number        </dt>        <dd class="col-sm-10">            '. htmlspecialchars($order->ProjectNumber).'        </dd>        <dt class="col-sm-2">            PO Number        </dt>        <dd class="col-sm-10">            '. htmlspecialchars($order->PurchaseOrderNumber).'        </dd>        <dt class="col-sm-2">            Project Name        </dt>        <dd class="col-sm-10">            '. htmlspecialchars($order->ProjectName).'        </dd>        <dt class="col-sm-2">            Notes        </dt>        <dd class="col-sm-10">			<code>'.            str_replace("\n", "<br>\n", htmlspecialchars($order->Notes)).'			</code>        </dd>	    <dt class="col-sm-2">            Status        </dt>        <dd class="col-sm-10">            '.  htmlspecialchars($order->Status).'        </dd>    </dl>	<table class="styled-table">		<thead>			<tr>				<th>					File Name				</th>				<th>					Content Type				</th>				<th>					Size				</th>				<th></th>			</tr>		</thead>		<tbody>';		foreach($files as $fileinfo)		{			$HTML .= "<tr>";            $HTML .="<td>".htmlspecialchars($fileinfo->FileName)."</td>";			$HTML .="<td>".htmlspecialchars($fileinfo->ContentType)."</td>";			$HTML .="<td>".$fileinfo->Length."</td>";			$HTML .="<td>";			if(file_exists($fileinfo->StoredFilePath))			{				$HTML .= "<a href='http://linux.kbs-cloud.com/fileupload/ViewFile.php?id=".$fileinfo->FileId."&download=false". 				($useViewOrderKey? "&viewOrderKey=".$order->ViewOrderKey : "" )."' target='_blank'>View</a> ";				$HTML .= "<a href='http://linux.kbs-cloud.com/fileupload/ViewFile.php?id=".$fileinfo->FileId."&download=true".				($useViewOrderKey? "&viewOrderKey=".$order->ViewOrderKey : "" )."'>Download</a>";			}			$HTML .= "</td>";            $HTML .= "</tr>";		}		$HTML .= "</tbody>";		$HTML .= "</table>";		        		if($includeViewLink)			$HTML .= "<a href='http://linux.kbs-cloud.com/fileupload/ViewOrder.php?id=".$order->OrderId.($useViewOrderKey? "&viewOrderKey=".$order->ViewOrderKey : "" )."'>View Order</a>";		else			$HTML .= "<a href='http://linux.kbs-cloud.com/fileupload/DownloadZip.php?id=".$order->OrderId.($useViewOrderKey? "&viewOrderKey=".$order->ViewOrderKey : "" )."'>Download ZIP</a>";	}	else		$HTML = "Order not found.";	return $HTML;}